const fs = require('fs');

const _ = require('lodash');

const utils = require('common/utils');

const compiled = _.template(
    fs.readFileSync(utils.p(__dirname + '/entry-template.js')).toString()
);

/**
 * 用于提供 hot replacement 的支持
 *
 * @def: entryPath => compiledEntryScript
 *  entryPath: string
 *  compiledEntryScript
 */
module.exports = function getEntryContent(entries) {
    entries = typeof entries === 'string' ? [entries] : entries
    return compiled({entries: entries})
};
